function contactFormToggle(){$("#modal-background").toggleClass("hide show")}function resetContactForm(){$("#topic-select").val("");$("#body-text").val("")}$("#contact-button").click(function(){contactFormToggle()});$("#modal-background").click(function(n){(n.target.id==="modal-background"||n.target.id==="contact-close")&&contactFormToggle()});$(function(){var n=$("#contact-form");$(n).submit(function(t){t.preventDefault();var i=$(n).serialize();$.ajax({type:"POST",url:$(n).attr("action"),data:i}).done(function(){toastr.success("Your message has been sent");resetContactForm();contactFormToggle()}).fail(function(){toastr.error("Your message was unable to be delivered")})})});